// MainPage.js
// src/components/MainPage.js
import React, { useEffect, useState } from 'react';
import ProductList from './ProductList';
import RecommendedProducts from './Recommendedproducts';
import axios from 'axios';

const MainPage = () => {
  const [products, setProducts] = useState([]);
  const [recommendedproducts, setRecommendedproducts] = useState([]);

  useEffect(() => {
    // Fetch products from the Django API
    axios.get('http://127.0.0.1:8000/api1/products')
      .then(response => {
        setProducts(response.data); // Assuming response contains all products
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });

    // Fetch recommended products from a different API endpoint or similar logic
    axios.get('http://127.0.0.1:8000/api1/recommended-products')
      .then(response => {
        setRecommendedproducts(response.data); // Assuming response contains recommended products
      })
      .catch(error => {
        console.error('Error fetching recommended products:', error);
      });
  }, []);

  return (
    <div className="container mt-4">
      <h2>Our Products</h2>
      <ProductList products={products} />

      <h2 className="mt-5">Recommended for You</h2>
      <Recommendedproducts recommendedproducts={recommendedproducts} />
    </div>
  );
};


export default MainPage;
