import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProductList from './ProductList';
import RecommendedProducts from './Recommendedproducts'; // Ensure the correct import

const MainPage = () => {
  const [products, setProducts] = useState([]);
  const [recommendedProducts, setRecommendedProducts] = useState([]);

  useEffect(() => {
    // Fetch all products
    axios.get('http://127.0.0.1:8000/api1/products')
      .then(response => {
        setProducts(response.data); // Assuming response.data is an array
      })
      .catch(error => {
        console.error('Error fetching products:', error);
      });

    // Fetch recommended products
    axios.get('http://127.0.0.1:8000/api1/recommended-products')
      .then(response => {
        console.log('Recommended Products:', response.data); // Check the structure of the response
        setRecommendedProducts(response.data); // Make sure it's an array
      })
      .catch(error => {
        console.error('Error fetching recommended products:', error);
      });
  }, []);

  return (
    <div className="container mt-4">
      <h2>Our Products</h2>
      <ProductList products={products} />

      <h2 className="mt-5">Recommended for You</h2>
      <RecommendedProducts recommendedProducts={recommendedProducts} />
    </div>
  );
};

export default MainPage;
