/*// MainPage.js
import React from 'react';
import ProductList from './ProductList';
import RecommendedProducts from './Recommendedproducts';

const MainPage = ({ products, recommendedProducts }) => {
  return (
    <div className="container mt-4">
      <h2>Our Products</h2>
      <ProductList products={products} />

      <h2 className="mt-5">Recommended for You</h2>
      <RecommendedProducts recommendedProducts={recommendedProducts} />
    </div>
  );
};

export default MainPage;
/*/
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ProductCard from './ProductCard'; // Assuming you have a ProductCard component to display each product

const MainPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch products data from your API endpoint
    axios
      .get('http://127.0.0.1:8000/api1/products/') // Your Django API endpoint
      .then((response) => {
        setProducts(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error loading products: {error.message}</div>;
  }

  return (
    <div>
      <h1>Featured Products</h1>
      <div className="product-list">
        {products.map((product) => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default MainPage;
