import React, { useState } from 'react';
import axios from 'axios';

const SignupPage = () => {
  // State for storing form data
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  // State for handling form validation errors
  const [errors, setErrors] = useState({
    name: '',
    email: '',
    password: '',
  });

  // State for handling success or failure messages
  const [message, setMessage] = useState('');

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Validate form fields
  const validateForm = () => {
    let isValid = true;
    let errorMessages = { name: '', email: '', password: '' };

    if (!formData.name) {
      errorMessages.name = 'Name is required.';
      isValid = false;
    }
    if (!formData.email) {
      errorMessages.email = 'Email is required.';
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errorMessages.email = 'Please enter a valid email address.';
      isValid = false;
    }
    if (!formData.password) {
      errorMessages.password = 'Password is required.';
      isValid = false;
    } else if (formData.password.length < 6) {
      errorMessages.password = 'Password must be at least 6 characters long.';
      isValid = false;
    }

    setErrors(errorMessages);
    return isValid;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form before submission
    if (validateForm()) {
      try {
        const response = await axios.post('http://127.0.0.1:8000/api/accounts/signup/', formData);
        setMessage('Signup successful!');
        console.log('Form data submitted:', response.data);
        setFormData({ name: '', email: '', password: '' }); // Reset form
        setErrors({ name: '', email: '', password: '' }); // Clear errors
      } catch (error) {
        console.error('Error during signup:', error.response?.data || error.message);
        setMessage('Signup failed. Please try again.');
      }
    }
  };

  return (
    <div className="container">
      <h2>Sign Up</h2>
      {message && <div className={`alert ${message.includes('successful') ? 'alert-success' : 'alert-danger'}`}>{message}</div>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">
            Name
          </label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
          {errors.name && <div className="text-danger">{errors.name}</div>}
        </div>

        <div className="mb-3">
          <label htmlFor="email" className="form-label">
            Email address
          </label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          {errors.email && <div className="text-danger">{errors.email}</div>}
        </div>

        <div className="mb-3">
          <label htmlFor="password" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          {errors.password && <div className="text-danger">{errors.password}</div>}
        </div>

        <button type="submit" className="btn btn-primary">
          Sign Up
        </button>
      </form>
    </div>
  );
};

export default SignupPage;
